import React, { useState, useEffect }  from "react"
// import DateTimePicker from 'react-datetime-picker'

const SlotPage = () => {
    const [date, setDate] = useState('');
    const [hour, setHour] = useState('');
    const [time, setTime] = useState('');
    const [data, setData] = React.useState([]);

    console.log(date,"date")
    console.log(hour,"hour")
    console.log(time,"time")





    const fetchSlots = (date, hour, time) => {
        const url =    `http://localhost:3000/slots?date=${date}T${hour}:00+00:00&minutes=${time}`;
    //  `https://localhost:3000/slots?date=${date}T${hour}:00+00:00&minutes=${time}`;
      console.log(url, "url")
      fetch(url)
        .then((response) => response.json())
        .then((json) => setData(json['results']))
        .catch((error) => console.log(error));
    };


    const handleSubmit = (e) => {
        e.preventDefault();
        fetchSlots(date, hour, time)
    }

        return(
            <div>
                <h1>Slot Booking App</h1>
            <form onSubmit={e => {handleSubmit(e)}}>
            <label>
              Select date between 1st of January 2022 and 28th of February 2022 and duration
              {/* <DateTimePicker /> */}
              <input type="date" onChange={(e) => setDate(e.target.value)} />
              <input type="time" onChange={(e) => setHour(e.target.value)} ></input>
              <input 
              type="input" 
            pattern="[+-]?\d+(?:[.,]\d+)?"
            placeholder="Enter amount"
            onChange={(e) => setTime(e.target.value)} 
             />

            </label>
            <input type="submit" value="Submit" />
          </form>
          <div>Available slots {data}</div>
          </div>
        )

}
export default SlotPage
