import React, { useState, useEffect }  from "react"
// import DateTimePicker from 'react-datetime-picker'

const SlotPage = () => {
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [data, setData] = useState([]);

    console.log(date,"date")
    console.log(time,"time")





    const fetchSlots = (date, time) => {
      const url = `http://localhost:3000/slots?date=${date}T00:00:00+00:00&minutes=${time}`;
      fetch(url)
        .then((response) => setData(response.body))
        .catch((error) => console.log(error));
    };
//     const fetchSlots = (date, hour, time) => {
//     useEffect(() => {
//         fetch( `http://localhost:3000/slots?date=${date}T${hour}:00+00:00&minutes=${time}`).then(
//             res => setState(res.data)
//         )
//     })
// }

    const handleSubmit = (e) => {
        e.preventDefault();
        fetchSlots(date, time)
    }

        return(
            <div>
                <h1>Slot Booking App</h1>
            <form onSubmit={e => {handleSubmit(e)}}>
            <label>
              Select date between 1st of January 2022 and 28th of February 2022 and duration
              {/* <DateTimePicker /> */}
              <input type="date" onChange={(e) => setDate(e.target.value)} />
              <input 
              type="input" 
            pattern="[+-]?\d+(?:[.,]\d+)?"
            placeholder="Enter amount"
            onChange={(e) => setTime(e.target.value)} 
             />

            </label>
            <input type="submit" value="Submit" />
          </form>
         {console.log(data, "data")}
          </div>
        )

}
export default SlotPage
