import React, { useState, useEffect } from "react";

const SlotPage = () => {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [list, setList] = useState([]);

  // const fetchSlots = (date, time) => {
  //   const url = `http://localhost:3000/slots?date=${date}T00:00:00+00:00&minutes=${time}`;
  //   fetch(url)
  //     .then((response) => console.log(response.data, "inside"))
  //     .catch((error) => console.log(error));

  //   // console.log(response.body, "after save");
  // };

  useEffect(() => {
    let mounted = true;
    getList("2022-02-01", 44).then((items) => {
      if (mounted) {
        setList(items);
      }
    });
    return () => (mounted = false);
  }, []);

  const getList = (date, time) => {
    return fetch(
      `http://localhost:3000/slots?date=${date}T00:00:00+00:00&minutes=${time}`
    ).then((data) => data.json());
  };

  const formatDate = (date) => {
    time = date.slice(11, 16);
    return time;
  };

  // const fetchSlots = (date, time) => {
  //   fetch(
  //     `http://localhost:3000/slots?date=${date}T00:00:00+00:00&minutes=${time}`
  //   )
  //     .then((res) => res.json())
  //     .then((res) => this.setState({ planets: res }))
  //     .catch(() => this.setState({ hasErrors: true }));
  // };

  // useEffect(() => {
  //   console.log(slots);
  // });

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   fetchSlots(date, time);
  // };

  return (
    <div>
      <h1>Slot Booking App</h1>
      <form
        onSubmit={(e) => {
          handleSubmit(e);
        }}
      >
        <label>
          Select date between 1st of January 2022 and 28th of February 2022 and
          duration
          <input type="date" onChange={(e) => setDate(e.target.value)} />
          <input
            type="input"
            pattern="[+-]?\d+(?:[.,]\d+)?"
            placeholder="Enter amount"
            onChange={(e) => setTime(e.target.value)}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <div>
        <h1>Available Slot List</h1>
        <ul>
          {list.map((item, index) => (
            <li key={index} index={index}>
              {formatDate(item.start_time)} - {item.end_time}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};
export default SlotPage;
